import React from "react";
import { createChatBotMessage } from 'react-chatbot-kit';
import DogPicture from '../components/DogPicture.jsx'

const config = {
    botName:"Beta",
  initialMessages: [createChatBotMessage(`Type hello to start!`)],
  widgets: [
    {
      widgetName: 'dogPicture',
      widgetFunc: (props) => <DogPicture {...props} />,
    },
  ],
  customStyles: {
    botMessageBox: {
      backgroundColor: '#03a394',
    },
    chatButton: {
      backgroundColor: '#03a394',
    },
  },
  customComponents: {
    // Replaces the default header
   header: () => <div style={{ backgroundColor: '#03a394', padding: "5px", borderRadius: "3px",fontSize:"20px" }}>Conversation with Beta</div>
 },
};

export default config;
