import React, { useEffect } from 'react';
import { Widget, addResponseMessage } from 'react-chat-widget';
import config from '../chatbot/config.js';
import MessageParser from '../chatbot/MessageParser.js';
import ActionProvider from '../chatbot/ActionProvider.js';
import Chatbot from 'react-chatbot-kit'
import 'react-chatbot-kit/build/main.css'

import './style.css';
// import {myfunction} from './myfunction';


function Widgeticons() {
   const onBotclick =() =>{
      var x1 = document.getElementById("mybot");
      var y1 = document.getElementById("hide1");
      var y2 = document.getElementById("hide2");
      var y3 = document.getElementById("hide3");
      var y4 = document.getElementById("hide4");
      var y5 = document.getElementById("hide5");
        if (x1.style.display === "none") {
          x1.style.display = "block";
          y1.style.display = "none";  
          y2.style.display = "none";  
          y3.style.display = "none";  
          y4.style.display = "none"; 
          y5.style.display = "none"; 
         //  x.style.animation = "move-in-steps 8s infinite";

        } else {
          x1.style.display = "none";
          y1.style.display = "block";
          y2.style.display = "block";  
          y3.style.display = "block";  
          y4.style.display = "block"; 
          y5.style.display = "block"; 
        }
   }
    const shoot = () => {
        
        var m1 = document.getElementById("show1");
        var m2 = document.getElementById("show2");
        var m3 = document.getElementById("show3");
        var m4 = document.getElementById("show4");
        var m5 = document.getElementById("show5");
        if (m1.style.display === "none") {
          m1.style.display = "block";
         //  m1.style.animation = "move-in-steps 8s infinite";
        } else {
          m1.style.display = "none";
        }
        if (m2.style.display === "none") {
         m2.style.display = "block";
         m2.style.animation = "move-in-steps 8s infinite";
       } else {
         m2.style.display = "none";
       }
       if (m3.style.display === "none") {
         m3.style.display = "block";
         m3.style.animation = "move-in-steps 8s infinite";
       } else {
         m3.style.display = "none";
       }
       if (m4.style.display === "none") {
         m4.style.display = "block";
         m4.style.animation = "move-in-steps 8s infinite";
       } else {
         m4.style.display = "none";
       }
       if (m5.style.display === "none") {
         m5.style.display = "block";
         m5.style.animation = "move-in-steps 8s infinite";
       } else {
         m5.style.display = "none";
       }
      }
    
      // useEffect(() => {
      //   addResponseMessage('Welcome to this **awesome** chat!');
      // }, []);
    
      // const handleNewUserMessage = (newMessage) => {
      //   console.log(`New message incoming! ${newMessage}`);
      //   // Now send the message throught the backend API
      // };   
    
        
   
    return(<><div class='circle-container'>
    <div class="wrapper center">
         <div className="icon facebook deg0" id="show1 hide1">
            <div class="tooltip">
               Facebook
            </div>
            <span><i class="fab fa-facebook-f"></i></span>
         </div>
         <div className="icon twitter deg20" id="show2 hide2">
            <div class="tooltip">
               Twitter
            </div>
            <span><i class="fab fa-twitter"></i></span>
         </div>
         <div className="icon instagram deg45" id="show3 hide3">
            <div class="tooltip">
               Instagram
            </div>
            <span><i class="fab fa-instagram"></i></span>
         </div>
         <div className="icon github deg135" id="show4 hide4">
            <div class="tooltip">
               Github
            </div>
            <span><i class="fab fa-github "></i></span>
         </div>
         <div className="icon youtube deg135" id="show5 hide5">
            <div class="tooltip">
            chatbot
            </div>
            <button className="btn1" onClick={onBotclick}>
            <span><i class="fa-light fa-message-bot"></i></span>
            </button>
         </div>
         
         {/* <div id="myBot">
         <Chatbot
            config={config}
            messageParser={MessageParser}
            actionProvider={ActionProvider}
         />
         </div> */}
      </div>
     <div class="wrapper">
         <div class="icon expand">
            <div class="tooltip">
               Click 
            </div> 
            <button className='btnn' onClick={shoot}><span><i class="fa-solid fa-expand" ></i></span></button>
         </div>
     </div>
     
    </div>
</>
);
}
export default Widgeticons;
